{
  description = "A Nix-flake-based development environment";

  inputs = {
    nixpkgs.url = "github:nixos/nixpkgs/nixos-25.05";
  };

  outputs = { self , nixpkgs ,... }: let
    # system should match the system you are running on
    system = "x86_64-linux";
  in {
    devShells."${system}".default = let
      pkgs = import nixpkgs {
        inherit system;
      };
    in pkgs.mkShell {
      packages = with pkgs; [
        cmake
        ninja
        glslang # or shaderc
      ];

      buildInputs = with pkgs; [
        mesa # open gl
        libGL # compatibility
        libGLU
        pkg-config # helps cmake find dlibs
        glfw
        xorg.libX11
        xorg.libXext        
        xorg.libXrandr      
        xorg.libXcursor     
        xorg.libXrender     
        xorg.libXi  
        xorg.libXinerama 
      ];

      shellHook = ''
        echo "Entered dev env"
        export LD_LIBRARY_PATH="/run/opengl-driver/lib:/run/opengl-driver-32/lib";
      '';
    };
  };
}

int err=0;
char msg[256];
while( (err=glGetError())!=0 )
{
	sprintf(msg, "GL_ERROR=0x%x\n", err);
	#ifdef ANT_WINDOWS
		OutputDebugString(msg);
	#endif
	fprintf(stderr, msg);
}
